userDefinedLinkHintCss:
/* HUD */

iframe.vimiumHUDFrame {
    height: 30px;
    min-height: 30px;
}

div.vimiumHUD {
    background-color: #222;
    border-color: #f4f;
    border-radius: 0;
    border-style: solid;
    border-width: 0 3px 0 0;
    color: #f4f;
    font-weight: bold;
    padding: 8px;
    text-shadow: none;
}

/* Link Hints */

div > .vimiumHintMarker {
    background: #222;
    border-color: #f4f;
    border-radius: 0;
    border-style: solid;
    border-width: 0 0 0 3px;
    cursor: default;
    padding: 4px;
}

div > .vimiumHintMarker span {
    color: #f4f;
    cursor: default;
    font-size: 12px;
    font-weight: bold;
    text-shadow: none;
}

div > .vimiumActiveHintMarker span {
    color: #f8f!important;
}

/* Vomnibar */

#vomnibar {
    background: #333;
    border-radius: 0;
    box-shadow: none;
}

#vomnibar .vomnibarSearchArea {
    background-color: #222;
    border: none;
    border-color: #f4f;
    border-radius: 0;
    border-style: solid;
    border-width: 0 0 0 4px;
}

#vomnibar .vomnibarSearchArea > input {
    background-color: #222;
    border: none;
    color: #f4f;
}

#vomnibar ul {
    background-color: #333;
    border-radius: 0;
}

#vomnibar li {
    background-color: transparent;
    border-color: #F8F;
    border-style: solid;
    border-width: 0;
    margin-top: 10px;
    transition: background-color 0.1s ease;
}

#vomnibar li em,
#vomnibar li .vomnibarTitle {
    color: #fff;
}

#vomnibar li em .vomnibarMatch,
#vomnibar li .vomnibarTitle .vomnibarMatch,
#vomnibar li .vomnibarMatch {
    color: #f4f;
}

#vomnibar li .vomnibarUrl {
    color: #ccc;
}

#vomnibar li.vomnibarSelected {
    background-color: #444;
}
